mapper.insert:

				MERGE INTO TB_BOARD_LIKE BL
            USING DUAL
                ON (BL.LIKE_ID = #{likeId})
            WHEN MATCHED THEN
                UPDATE SET LIKE_YN = #{likeYn}
            WHEN NOT MATCHED THEN
							INSERT (
								LIKE_ID
								,DOC_ID
								,USER_ID
								,LIKE_YN
								,REG_DT
							)VALUES(
								SEQ_LIKE.NEXTVAL
								,#{docId}
								,#{userId}
								,#{likeYn}
								,SYSDATE
							)
